mapper.select:
select * from (
    select rownum r_num, a.* from (
     select * from Pharmacy order by pnum
    ) a
 ) where r_num BETWEEN #{begin} and #{end}

select * from (
select rownum r_num, a.* from (
select * from Pharmacy where


		shopName 


		fullAddress 


		pnum 


like '%'||#{searchf}||'%'
order by pnum
) a
) where r_num BETWEEN #{begin} and #{end}
